() => {
    const radius = 200
    const centerX = 400
    const centerY = 300

    return filteredEntities.map((entity, index) => {
      const angle = (index / filteredEntities.length) * 2 * Math.PI
      const x = centerX + radius * Math.cos(angle)
      const y = centerY + radius * Math.sin(angle)

      return {
        id: entity.id,
        x,
        y,
      }
    })
  }

