      case "renders":
        return "#10b981" // emerald-500
      case "uses":
        return "#8b5cf6" // violet-500
    }
  }

  const zoomIn = () => {
    setZoom((prev) => Math.min(prev + 0.1, 2))
  }

  const zoomOut = () => {
    setZoom((prev) => Math.max(prev - 0.1, 0.5))
  }

  if (loading) {
    return (
