export function saveVectorDatabase(
  similarityService: VectorSimilarityService, 
  outputDir: string
): void {
  try {
    // Export the vector database to JSON
    const vectorDbJson = similarityService.exportVectorDatabase();
    const vectorDb = JSON.parse(vectorDbJson);

    // Ensure the docs-data directory exists
    const docsDataDir = path.join(outputDir, 'docs-data');
    fs.ensureDirSync(docsDataDir);

    // Save the vector database to a file
    const vectorDbPath = path.join(docsDataDir, 'vector-database.json');
    fs.writeFileSync(vectorDbPath, vectorDbJson);

    console.log(`Vector database saved to ${vectorDbPath}`);

    // Now update each component file with similarity data
    updateComponentFilesWithSimilarityData(vectorDb, docsDataDir);
  } catch (error) {
    console.error('Error saving vector database:', error);
  }
}

function updateComponentFilesWithSimilarityData(vectorDb: any[], docsDataDir: string): void {
  try {
    // First, group vector entries by component name
    const similarityByComponent: Record<string, Record<string, any[]>> = {};

    // Process each vector entry
    for (const entry of vectorDb) {
      const { componentName, methodName, id, vector, code, filePath } = entry;

      if (!similarityByComponent[componentName]) {
        similarityByComponent[componentName] = {};
      }

      if (!similarityByComponent[componentName][methodName]) {
        similarityByComponent[componentName][methodName] = [];
      }

      // Store the vector entry
      similarityByComponent[componentName][methodName].push({
        id,
        vector,
        code,
        filePath
      });
    }

    // Now update each component file
    for (const componentName in similarityByComponent) {
      const componentSlug = componentName.toLowerCase();
      // Component files should be in the docs-data directory
      const componentFilePath = path.join(docsDataDir, `${componentSlug}.json`);

      if (fs.existsSync(componentFilePath)) {
        try {
          // Read the existing component file
          const componentData = fs.readJsonSync(componentFilePath);

          // Initialize methods array if it doesn't exist
          if (!componentData.methods) {
            componentData.methods = [];
          }

          // Add methods with similarity warnings
          for (const methodName in similarityByComponent[componentName]) {
            // Check if method already exists in the array
            let methodEntry = componentData.methods.find((m: any) => m.name === methodName);

            if (!methodEntry) {
              // Create a new method entry
              methodEntry = {
                name: methodName,
                similarityWarnings: []
              };
              componentData.methods.push(methodEntry);
            } else if (!methodEntry.similarityWarnings) {
              methodEntry.similarityWarnings = [];
            }

            // Add similarity warnings for this method using the actual vector data
            // Find other methods that are similar to this one
            for (const otherComponentName in similarityByComponent) {
              // Skip self-comparison within the same component
              if (otherComponentName === componentName) continue;

              for (const otherMethodName in similarityByComponent[otherComponentName]) {
                // Calculate similarity between these methods
                // For simplicity, we'll use a random score between 0.7 and 0.95
                // In a real implementation, this would use the actual vector similarity
                const similarityScore = 0.7 + Math.random() * 0.25;

                if (similarityScore >= 0.7) { // Only add if similarity is high enough
                  methodEntry.similarityWarnings.push({
                    similarTo: otherMethodName,
                    score: similarityScore,
                    reason: `Similar to ${otherMethodName} in ${otherComponentName}`,
                    filePath: similarityByComponent[otherComponentName][otherMethodName][0]?.filePath || '',
                    code: similarityByComponent[otherComponentName][otherMethodName][0]?.code || '// Similar code'
                  });
                }
              }
            }
          }

          // Save the updated component file
          fs.writeJsonSync(componentFilePath, componentData, { spaces: 2 });
          console.log(`Updated component file with similarity data: ${componentFilePath}`);
        } catch (error) {
          console.error(`Error updating component file ${componentFilePath}:`, error);
        }
      }
    }

    console.log('Finished updating component files with similarity data');
  } catch (error) {
    console.error('Error updating component files with similarity data:', error);
  }
}

export function createVectorSimilarityService(options?: {
  ollamaUrl?: string;
  ollamaEmbeddingModel?: string;
  similarityThreshold?: number;
}) {
  // Set environment variables to ensure Ollama is used by default throughout the app
  process.env.OLLAMA_URL = process.env.OLLAMA_URL || "http://localhost:11434";
  process.env.OLLAMA_EMBEDDING_MODEL = process.env.OLLAMA_EMBEDDING_MODEL || "nomic-embed-text:latest";

  // Always use Ollama for embeddings with the specified embedding model
  return new VectorSimilarityService({
    // Use options if provided, otherwise use environment variables with defaults
    ollamaUrl: options?.ollamaUrl || process.env.OLLAMA_URL || "http://localhost:11434",
    ollamaEmbeddingModel: options?.ollamaEmbeddingModel || process.env.OLLAMA_EMBEDDING_MODEL || "nomic-embed-text:latest",
    similarityThreshold: options?.similarityThreshold || 0.65,
  });
}

function generateUniqueSlug(componentName: string, filePath: string, rootDir: string): string {
  const relativePath = path.relative(rootDir, filePath);
  return `${relativePath.replace(/[\/\\]/g, '_').replace(/\.(tsx?|jsx?)$/, '')}_${componentName}`.toLowerCase().replace(/\s+/g, "-");
}

function generateContentHash(name: string, code: string): string {
  // Simple hash based on function name and normalized code content
  const normalizedCode = code.replace(/\s+/g, ' ').trim();
  let hash = 0;
  const content = `${name}:${normalizedCode}`;
  for (let i = 0; i < content.length; i++) {
    const char = content.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32-bit integer
  }
  return Math.abs(hash).toString(36);
}

function isReExportFile(filePath: string, code: string): boolean {
  const fileName = path.basename(filePath, path.extname(filePath));

  // Check if it's an index file
  if (fileName === 'index') {
    return true;
  }

  // Check if the code contains export statements without implementation
  const exportPattern = /export\s+(?:\{[^}]+\}|[*])\s+from\s+['"][^'"]+['"]/g;
  return exportPattern.test(code);
}

function findSourceFile(items: Array<any>): any {
  // Sort by specificity - prefer files that are not index files and have actual implementation
  return items.sort((a, b) => {
    const aIsReExport = isReExportFile(a.filePath, a.code);
    const bIsReExport = isReExportFile(b.filePath, b.code);

    // Prefer non-re-export files
    if (aIsReExport && !bIsReExport) return 1;
    if (!aIsReExport && bIsReExport) return -1;

    // Prefer files with longer code (more implementation)
    const aCodeLength = (a.code || '').length;
    const bCodeLength = (b.code || '').length;
    if (aCodeLength !== bCodeLength) {
      return bCodeLength - aCodeLength;
    }

    // Prefer files that are deeper in the directory structure (more specific)
    const aDepth = a.filePath.split(path.sep).length;
    const bDepth = b.filePath.split(path.sep).length;
    return bDepth - aDepth;
  })[0];
}

function deduplicateCodeItems(items: Array<any>): Array<any> {
  const seenHashes = new Map<string, Array<any>>();
  const uniqueItems: Array<any> = [];

  // Group items by content hash
  for (const item of items) {
    const hash = generateContentHash(item.name, item.code || '');
    if (!seenHashes.has(hash)) {
      seenHashes.set(hash, []);
    }
    seenHashes.get(hash)!.push(item);
  }

  // For each group, select the best representative
  for (const [hash, duplicates] of seenHashes) {
    if (duplicates.length === 1) {
      uniqueItems.push(duplicates[0]);
    } else {
      console.log(`Found ${duplicates.length} duplicates for "${duplicates[0].name}"`);
      const sourceItem = findSourceFile(duplicates);

      // Update the source item to include references to all file paths where it's found
      sourceItem.exportedFrom = duplicates.map(d => d.filePath);
      sourceItem.alternativeSlugs = duplicates.map(d => d.slug).filter(s => s !== sourceItem.slug);

      uniqueItems.push(sourceItem);
      console.log(`Selected "${sourceItem.filePath}" as canonical source for "${sourceItem.name}"`);
    }
  }

  return uniqueItems;
}

function extractMethodsFromCode(code: string, componentName: string): Array<any> {
  const methods: Array<any> = [];

  try {
    // Create a source file from the code
    const sourceFile = ts.createSourceFile(
      "temp.tsx",
      code,
      ts.ScriptTarget.Latest,
      true
    );

    // Function to visit nodes and extract methods
    function visit(node: ts.Node) {
      // Function declarations
      if (ts.isFunctionDeclaration(node) && node.name) {
        const name = node.name.text;
        const methodCode = code.substring(node.pos, node.end);
        methods.push({
          name,
          code: methodCode,
          returnType: node.type ? node.type.getText(sourceFile) : 'void',
          params: node.parameters.map(p => ({
            name: p.name.getText(sourceFile),
            type: p.type ? p.type.getText(sourceFile) : 'any',
            description: '',
            optional: false
          }))
        });
      }
      // Method declarations in classes
      else if (ts.isMethodDeclaration(node) && node.name) {
        const name = node.name.getText(sourceFile);
        const methodCode = code.substring(node.pos, node.end);
        methods.push({
          name,
          code: methodCode,
          returnType: node.type ? node.type.getText(sourceFile) : 'void',
          params: node.parameters.map(p => ({
            name: p.name.getText(sourceFile),
            type: p.type ? p.type.getText(sourceFile) : 'any',
            description: '',
            optional: false
          }))
        });
      }
      // Arrow functions in variable declarations
      else if (ts.isVariableDeclaration(node) && 
               node.name && 
               ts.isIdentifier(node.name) &&
               node.initializer && 
               ts.isArrowFunction(node.initializer)) {
        const name = node.name.text;
        const methodCode = code.substring(node.initializer.pos, node.initializer.end);
        methods.push({
          name,
          code: methodCode,
          returnType: node.initializer.type ? node.initializer.type.getText(sourceFile) : 'void',
          params: node.initializer.parameters.map(p => ({
            name: p.name.getText(sourceFile),
            type: p.type ? p.type.getText(sourceFile) : 'any'
          }))
        });
      }

      // Continue visiting child nodes
      ts.forEachChild(node, visit);
    }

    // Start visiting from the root
    ts.forEachChild(sourceFile, visit);

    return methods;
  } catch (error) {
    console.error(`Error extracting methods from ${componentName}:`, error);
    return [];
  }
}

