  private calculateMethodHash(componentName: string, method: any): string {
    const dataToHash = {
      componentName,
      methodName: method.name || "unnamed",
      params: method.params || [],
      returnType: method.returnType || "unknown",
      sourceCode: method.sourceCode || ""
    };

