export async function generateDocumentation(
  components: any[],
  options: GenerateDocumentationOptions = {}
) {
  const outputDir = options.output || "docs-data";
  fs.ensureDirSync(outputDir);

  const componentIndex = components.map((component) => ({
    name: component.name,
    slug: component.slug,
    filePath: component.filePath,
  }));

  fs.writeJSONSync(path.join(outputDir, "component-index.json"), componentIndex, {
    spaces: 2,
  });

  for (const component of components) {
    if (component.slug) {
      fs.writeJSONSync(
        path.join(outputDir, `${component.slug}.json`),
        component,
        { spaces: 2 }
      );
    }
  }

  console.log(`Generated documentation for ${components.length} components`);
}

function parseSyntax(code: string): boolean {
  try {
    // Basic parsing - in a real implementation, this would be more sophisticated
    // Ensure we can handle all JavaScript and TypeScript syntax
    const patterns = [
      // Classes
      /class\s+\w+/,
      // Class methods
      /\w+\s*\([^)]*\)\s*{/,
      // Arrow functions
      /\([^)]*\)\s*=>\s*[{(]/,
      // Function declarations
      /function\s+\w+\s*\([^)]*\)/,
      // Interfaces
      /interface\s+\w+/,
      // Types
      /type\s+\w+\s*=/,
      // JSX/TSX
      /<[A-Z]\w+[^>]*>/,
      // Async functions
      /async\s+\w+\s*\(/,
      // Generators
      /function\s*\*\s*\w+/,
      // Destructuring
      /const\s*{\s*[^}]+\s*}\s*=/,
      // Rest parameters
      /\.\.\.\w+/,
      // Decorators
      /@\w+/,
    ];

    return patterns.some((pattern) => pattern.test(code));
  } catch (error) {
    return false;
  }
}

