          </Button>
        </div>
      </div>
      <AnimatePresence>
        {description && (
          <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 10 }}>
            <Card className="mt-4 bg-white/5 border-violet-500/30">
