function toSlug(name: string): string {
    return name.toLowerCase().replace(/([A-Z])/g, '-$1').replace(/^-/, '').replace(/\s+/g, "-");
  }

function addRelationship(source: string, target: string, type: RelationshipType, weight = 1, context?: string, extra?: Partial<CallRelationship>) {
    const targetSlug = toSlug(target);

    if (source === targetSlug || (availableEntities && !availableEntities.has(targetSlug))) {
      return;
    }

    const relationshipKey = `${source}->${targetSlug}:${type}`;
    if (!seenRelationships.has(relationshipKey)) {
      seenRelationships.add(relationshipKey);

      if (type === "uses" && extra) {
        const callRelationship: CallRelationship = {
          source,
          target: targetSlug,
          type: "uses",
          weight,
          context,
          sourceFile: extra.sourceFile!,
          sourceLine: extra.sourceLine!,
          targetFunction: extra.targetFunction,
        };
        relationships.push(callRelationship);
      } else {
        relationships.push({
          source,
          target: targetSlug,
          type,
          weight,
          context
        });
      }
    }
  }

export async function parseComponents(
  options: ParserOptions
): Promise<ComponentDefinition[]> {
  const {
    rootDir,
    componentPath,
    excludePatterns = [],
    includePatterns = ["**/*.tsx", "**/*.jsx", "**/*.js", "**/*.ts"],
    maxDepth = Infinity,
    apiKey,
    similarityThreshold,
    useOllama,
    ollamaUrl,
    ollamaModel,
  } = options;

  // Initialize vector similarity service if API key is provided or Ollama is enabled
  let vectorSimilarityService: VectorSimilarityService | null = null;

  if (useOllama) {
    vectorSimilarityService = new VectorSimilarityService({
      useOllama: true,
      ollamaUrl,
      ollamaModel,
      similarityThreshold,
    });
    debug("Vector similarity service initialized with Ollama");
  } else if (apiKey) {
    vectorSimilarityService = new VectorSimilarityService({
      apiKey,
      similarityThreshold,
    });
    debug("Vector similarity service initialized with OpenAI");
  } else {
    // Always initialize with Ollama as fallback rather than skipping
    vectorSimilarityService = new VectorSimilarityService({
      useOllama: true,
      ollamaUrl: "http://localhost:11434", // Default Ollama URL
      ollamaModel: "nomic-embed-text:latest", // Default Ollama embedding model
      similarityThreshold: similarityThreshold || 0.85,
    });
    debug(
      "Vector similarity service initialized with default Ollama settings (fallback)"
    );
  }

  // Resolve absolute paths
  const absoluteRootDir = path.resolve(rootDir);

  debug("Absolute Root Dir:", absoluteRootDir);
  debug("Include Patterns:", includePatterns);
  debug("Exclude Patterns:", excludePatterns);

  // Find suitable tsconfig.json file
  const tsconfigPath = findTsConfig(absoluteRootDir);

  if (!tsconfigPath) {
    console.warn("No tsconfig.json found, using default TypeScript settings");
  }

  // Set up react-docgen-typescript parser
  const parserOptions = {
    propFilter: (prop: any) => {
      return !prop.parent || !prop.parent.fileName.includes("node_modules");
    },
    shouldExtractLiteralValuesFromEnum: true,
    shouldRemoveUndefinedFromOptional: true,
  };

  const parser = tsconfigPath 
    ? reactDocgen.withCustomConfig(tsconfigPath, parserOptions)
    : reactDocgen.withDefaultConfig(parserOptions);

  // First pass: Extract all available entities for relationship validation
  debug(`First pass: Extracting all entities...`);
  const availableEntities = await extractAllEntities(
    absoluteRootDir,
    includePatterns,
    excludePatterns
  );
  debug(`Found ${availableEntities.size} entities in codebase`);

  // Parse components with entity validation
  debug(`Second pass: Starting component parsing with react-docgen-typescript...`);
  const allParsedComponents = await parseSingleComponentFile(
    {
      rootDir,
      componentPath,
      excludePatterns,
      includePatterns,
      maxDepth,
      availableEntities,
    },
    parser
  );

  debug(`Parsed ${allParsedComponents.length} components initially`);

  // Only run similarity analysis if we have a vector service and components with methods
  if (vectorSimilarityService) {
    debug(`Starting similarity analysis pass...`);

    // Perform similarity analysis
    await processComponentListSimilarities(
      allParsedComponents,
      vectorSimilarityService
    );

    debug(`Similarity analysis completed`);
  } else {
    debug("Skipping similarity analysis (no vector service)");
  }

  return allParsedComponents;
}

export async function buildEntityDeclarationMap(
  rootDir: string,
  includePatterns: string[] = ["**/*.tsx", "**/*.jsx", "**/*.js", "**/*.ts"],
  excludePatterns: string[] = ["node_modules/**", "**/node_modules/**"]
): Promise<Map<string, EntityDeclaration>> {
  const entityMap = new Map<string, EntityDeclaration>();

  try {
    const files = await glob(includePatterns, {
      cwd: rootDir,
      ignore: excludePatterns,
      absolute: true
    });

    for (const filePath of files) {
      try {
        const fileContent = await fs.readFile(filePath, 'utf-8');
        const declarations = extractEntityDeclarations(fileContent, filePath);

        // Add each declaration to the map
        declarations.forEach(decl => {
          // If we already have this entity, prefer exported versions
          const existing = entityMap.get(decl.entitySlug);
          if (!existing || 
              (decl.exportType !== 'none' && existing.exportType === 'none') ||
              (decl.exportType === 'default' && existing.exportType === 'named')) {
            entityMap.set(decl.entitySlug, decl);
          }
        });
      } catch (error) {
        debug(`Error processing file ${filePath}:`, error);
      }
    }

    debug(`Built entity map with ${entityMap.size} entities`);
    return entityMap;
  } catch (error) {
    console.error('Error building entity declaration map:', error);
    return entityMap;
  }
}

export async function parseAllCodeItems(rootDir: string): Promise<CodeItem[]> {
  const patterns = ["**/*.js", "**/*.jsx", "**/*.ts", "**/*.tsx"];
  const ignore = ["**/node_modules/**"];
  const files = await glob(patterns, {
    cwd: rootDir,
    ignore,
    absolute: true,
  });
  const allItems: CodeItem[] = [];
  for (const filePath of files) {
    try {
      const content = await fs.readFile(filePath, "utf-8");
      const items = extractAllTopLevelCodeItems(content).map(item => ({
        ...item,
        filePath,
        type: item.kind, // Map kind to type for compatibility
      }));
      allItems.push(...items);
    } catch (err) {
      debug(`Failed to parse file: ${filePath}`, err);
    }
  }
  return allItems;
}

export function debug(...args: any[]): void {
  if (DEBUG) console.log("[DEBUG]", ...args);
}

export function resolveImportPath(
  importPath: string,
  currentFilePath: string,
  rootDir: string
): string | null {
  try {
    debug(`Resolving import: ${importPath} from ${currentFilePath}`);

    if (importPath.startsWith(".")) {
      // Relative import path
      const baseDir = path.dirname(currentFilePath);

      // Try resolving with different extensions
      const extensions = [".tsx", ".jsx", ".ts", ".js"];

      // First try direct path with extensions
      for (const ext of extensions) {
        const resolvedPath = path.resolve(baseDir, `${importPath}${ext}`);
        if (fs.existsSync(resolvedPath)) {
          debug(`  Resolved to: ${resolvedPath}`);
          return resolvedPath;
        }
      }

      // Then try as directory with index files
      const dirPath = path.resolve(baseDir, importPath);
      if (fs.existsSync(dirPath) && fs.statSync(dirPath).isDirectory()) {
        for (const ext of extensions) {
          const indexPath = path.resolve(dirPath, `index${ext}`);
          if (fs.existsSync(indexPath)) {
            debug(`  Found index file with extension: ${ext}`);
            return indexPath;
          }
        }
      }
    }

    return null;
  } catch (error) {
    console.error(`Error resolving import path ${importPath}:`, error);
    return null;
  }
}

export function shouldIncludeFile(
  filePath: string,
  rootDir: string,
  includePatterns: string[],
  excludePatterns: string[]
): boolean {
  // Get relative path for pattern matching
  const relativePath = path.relative(rootDir, filePath);

  // Check include patterns
  const included = includePatterns.some((pattern) => {
    const matches = minimatch(relativePath, pattern);
    debug(`  Include pattern ${pattern}: ${matches ? "" : ""}`);
    return matches;
  });

  // Check exclude patterns
  const excluded = excludePatterns.some((pattern) => {
    const matches = minimatch(relativePath, pattern);
    debug(`  Exclude pattern ${pattern}: ${matches ? "" : ""}`);
    return matches;
  });

  return included && !excluded;
}

export function findTsConfig(rootDir: string): string | null {
  // First check in the specified root directory
  let tsconfigPath = path.join(rootDir, "tsconfig.json");
  debug("Looking for tsconfig at:", tsconfigPath);

  // Check if the tsconfig exists, if not, try looking for it in parent directories
  if (!fs.existsSync(tsconfigPath)) {
    debug("tsconfig not found, looking in parent directories");
    let currentDir = rootDir;
    const rootDrive = path.parse(currentDir).root;

    while (currentDir !== rootDrive) {
      const testPath = path.join(currentDir, "tsconfig.json");
      debug("Checking for tsconfig at:", testPath);
      if (fs.existsSync(testPath)) {
        tsconfigPath = testPath;
        debug("Found tsconfig at:", tsconfigPath);
        return tsconfigPath;
      }
      currentDir = path.dirname(currentDir);
    }
  } else {
    return tsconfigPath;
  }

  // Fall back to the project's main tsconfig if none found
  debug("No tsconfig found in parent directories, falling back to project tsconfig");
  const projectRoot = path.resolve(__dirname, "../../..");
  tsconfigPath = path.join(projectRoot, "tsconfig.json");

  if (fs.existsSync(tsconfigPath)) {
    return tsconfigPath;
  }

  return null;
}

export function extractImportedComponentPaths(
  fileContent: string,
  currentFilePath: string,
  rootDir: string
): string[] {
  const importPaths: string[] = [];
  // Track processed paths to avoid circular dependencies
  const processedPaths = new Set<string>();

  try {
    // Create a simpler but more robust regex to match all imports
    const importRegex = /import\s+.*?from\s+['"]([^'"]+)['"]/g;

    let match;
    while ((match = importRegex.exec(fileContent)) !== null) {
      const importPath = match[1]; // The captured import path
      debug(
        `Found import: "${importPath}" in ${path.basename(currentFilePath)}`
      );

      // Only process relative imports
      if (importPath.startsWith(".")) {
        // Avoid re-processing the same paths
        if (processedPaths.has(importPath)) {
          debug(`  Skipping already processed import: ${importPath}`);
          continue;
        }

        processedPaths.add(importPath);

        // Resolve the import path
        const resolvedPath = resolveImportPath(
          importPath,
          currentFilePath,
          rootDir
        );

        if (resolvedPath) {
          debug(`  Adding resolved path: ${resolvedPath}`);
          importPaths.push(resolvedPath);
        } else {
          debug(`  Could not resolve import path: ${importPath}`);
        }
      } else {
        debug(`  Skipping non-relative import: ${importPath}`);
      }
    }

    return importPaths;
  } catch (error) {
    console.error(
      `Error extracting import paths from ${currentFilePath}:`,
      error
    );
    return [];
  }
}

export function analyzeCodebase(components: ComponentDefinition[]): CodeAnalysisResult {
  // Extract code blocks for duplicate detection
  const codeBlocks = extractCodeBlocks(components);

  // Detect duplicates
  const duplicates = detectDuplicates(codeBlocks, 0.7, 0.95);

  // Detect circular dependencies
  const entitiesForCircularCheck = components.map(comp => ({
    slug: comp.slug || comp.name.toLowerCase(),
    imports: comp.imports || [],
    filePath: comp.filePath
  }));
  const circularDependencies = detectCircularDependencies(entitiesForCircularCheck);

  // Calculate quality metrics
  const qualityMetrics = calculateQualityMetrics(components, duplicates, circularDependencies);

  // Generate recommendations
  const recommendations = generateRecommendations(components, duplicates, circularDependencies, qualityMetrics);

  return {
    components,
    duplicates,
    circularDependencies,
    qualityMetrics,
    recommendations
  };
}

function calculateQualityMetrics(
  components: ComponentDefinition[],
  duplicates: DuplicateCodeMatch[],
  circularDependencies: CircularDependency[]
): CodeQualityMetrics {
  const totalComponents = components.length;
  const totalRelationships = components.reduce((sum, comp) => sum + (comp.relationships?.length || 0), 0);

  // Calculate average complexity (based on methods and relationships)
  const averageComplexity = totalComponents === 0 ? 0 : 
    components.reduce((sum, comp) => {
      const methodComplexity = (comp.methods?.length || 0) * 2;
      const relationshipComplexity = (comp.relationships?.length || 0);
      return sum + methodComplexity + relationshipComplexity;
    }, 0) / totalComponents;

  // Calculate duplicate code percentage
  const duplicateCodePercentage = totalComponents === 0 ? 0 :
    (duplicates.filter(d => d.similarity >= 0.85).length / totalComponents) * 100;

  // Count prop drilling issues
  const propDrillingIssues = components.reduce((sum, comp) => 
    sum + (comp.propDrilling?.length || 0), 0);

  // Calculate maintainability score (0-100)
  let maintainabilityScore = 100;

  // Deduct points for issues
  maintainabilityScore -= Math.min(duplicateCodePercentage * 2, 30); // Max 30 points for duplicates
  maintainabilityScore -= Math.min(circularDependencies.length * 10, 25); // Max 25 points for circular deps
  maintainabilityScore -= Math.min(propDrillingIssues * 2, 15); // Max 15 points for prop drilling
  maintainabilityScore -= Math.min((averageComplexity - 5) * 3, 20); // Max 20 points for complexity

  maintainabilityScore = Math.max(0, maintainabilityScore);

  // Determine overall health
  let overallHealth: 'excellent' | 'good' | 'fair' | 'poor';
  if (maintainabilityScore >= 85) overallHealth = 'excellent';
  else if (maintainabilityScore >= 70) overallHealth = 'good';
  else if (maintainabilityScore >= 50) overallHealth = 'fair';
  else overallHealth = 'poor';

  return {
    totalComponents,
    totalRelationships,
    averageComplexity: Math.round(averageComplexity * 10) / 10,
    duplicateCodePercentage: Math.round(duplicateCodePercentage * 10) / 10,
    circularDependencyCount: circularDependencies.length,
    propDrillingIssues,
    maintainabilityScore: Math.round(maintainabilityScore),
    overallHealth
  };
}

function generateRecommendations(
  components: ComponentDefinition[],
  duplicates: DuplicateCodeMatch[],
  circularDependencies: CircularDependency[],
  metrics: CodeQualityMetrics
): CodeRecommendation[] {
  const recommendations: CodeRecommendation[] = [];

  // Duplicate code recommendations
  const { exactDuplicates, nearDuplicates } = groupDuplicatesBySeverity(duplicates);

  if (exactDuplicates.length > 0) {
    recommendations.push({
      type: 'duplicate',
      severity: 'high',
      title: 'Exact Duplicate Code Detected',
      description: `Found ${exactDuplicates.length} exact duplicates that should be consolidated immediately.`,
      affectedComponents: [...new Set(exactDuplicates.flatMap(d => [d.sourceEntity, d.targetEntity]))],
      suggestedActions: [
        'Extract common functionality into shared utilities or custom hooks',
        'Create reusable components for repeated UI patterns',
        'Use composition patterns to reduce code duplication',
        'Consider implementing a shared library for common business logic'
      ]
    });
  }

  if (nearDuplicates.length > 0) {
    recommendations.push({
      type: 'duplicate',
      severity: 'medium',
      title: 'Similar Code Patterns Found',
      description: `Found ${nearDuplicates.length} near-duplicate code blocks that could benefit from refactoring.`,
      affectedComponents: [...new Set(nearDuplicates.flatMap(d => [d.sourceEntity, d.targetEntity]))],
      suggestedActions: [
        'Review similar code patterns for potential consolidation',
        'Extract common patterns into reusable functions',
        'Consider using strategy pattern for similar but different implementations'
      ]
    });
  }

  // Circular dependency recommendations
  if (circularDependencies.length > 0) {
    const criticalCircular = circularDependencies.filter(cd => cd.severity === 'high');

    recommendations.push({
      type: 'circular-dependency',
      severity: criticalCircular.length > 0 ? 'critical' : 'high',
      title: 'Circular Dependencies Detected',
      description: `Found ${circularDependencies.length} circular dependencies that can cause runtime issues and make code harder to maintain.`,
      affectedComponents: [...new Set(circularDependencies.flatMap(cd => cd.cycle))],
      suggestedActions: [
        'Break circular dependencies by introducing interfaces or abstract classes',
        'Move shared logic to a separate module',
        'Use dependency injection to invert control',
        'Consider restructuring the module hierarchy'
      ]
    });
  }

  // Complexity recommendations
  if (metrics.averageComplexity > 10) {
    const complexComponents = components
      .filter(comp => (comp.methods?.length || 0) > 8)
      .map(comp => comp.name);

    recommendations.push({
      type: 'complexity',
      severity: metrics.averageComplexity > 15 ? 'high' : 'medium',
      title: 'High Component Complexity',
      description: `Average component complexity is ${metrics.averageComplexity}, which may indicate over-complex components.`,
      affectedComponents: complexComponents,
      suggestedActions: [
        'Break down large components into smaller, focused components',
        'Extract complex logic into custom hooks',
        'Use composition to reduce component responsibility',
        'Consider splitting components by concern (UI vs logic)'
      ]
    });
  }

  // Prop drilling recommendations
  if (metrics.propDrillingIssues > 3) {
    const propDrillingComponents = components
      .filter(comp => comp.propDrilling && comp.propDrilling.length > 0)
      .map(comp => comp.name);

    recommendations.push({
      type: 'prop-drilling',
      severity: metrics.propDrillingIssues > 6 ? 'high' : 'medium',
      title: 'Prop Drilling Issues',
      description: `Detected ${metrics.propDrillingIssues} instances of potential prop drilling, which can make components tightly coupled.`,
      affectedComponents: propDrillingComponents,
      suggestedActions: [
        'Consider using React Context for shared state',
        'Implement state management solutions like Redux or Zustand',
        'Use component composition to avoid passing props through multiple layers',
        'Extract shared logic into custom hooks'
      ]
    });
  }

  // Coupling recommendations
  const highlyConnectedComponents = components
    .filter(comp => (comp.relationships?.length || 0) > 8)
    .map(comp => comp.name);

  if (highlyConnectedComponents.length > 0) {
    recommendations.push({
      type: 'coupling',
      severity: 'medium',
      title: 'High Component Coupling',
      description: `Found ${highlyConnectedComponents.length} components with many dependencies, indicating tight coupling.`,
      affectedComponents: highlyConnectedComponents,
      suggestedActions: [
        'Review and reduce unnecessary dependencies',
        'Use interfaces to decouple concrete implementations',
        'Apply the Single Responsibility Principle',
        'Consider using event-driven architecture for loose coupling'
      ]
    });
  }

  // Positive reinforcement for good practices
  if (recommendations.length === 0) {
    recommendations.push({
      type: 'complexity',
      severity: 'low',
      title: 'Excellent Code Quality',
      description: 'Your codebase demonstrates good practices with minimal issues detected.',
      affectedComponents: [],
      suggestedActions: [
        'Continue following current coding practices',
        'Consider adding more unit tests to maintain quality',
        'Document architectural decisions for future maintainers',
        'Regular code reviews help maintain this quality level'
      ]
    });
  }

  return recommendations;
}

export function getHealthScoreColor(score: number): string {
  if (score >= 85) return 'text-green-600';
  if (score >= 70) return 'text-blue-600';
  if (score >= 50) return 'text-yellow-600';
  return 'text-red-600';
}

export function getHealthBadgeVariant(health: string): 'default' | 'secondary' | 'destructive' {
  switch (health) {
    case 'excellent':
    case 'good':
      return 'default';
    case 'fair':
      return 'secondary';
    case 'poor':
      return 'destructive';
    default:
      return 'secondary';
  }
}

export function extractCodeBlocks(components: ComponentDefinition[]): CodeBlock[] {
  const blocks: CodeBlock[] = [];

  for (const component of components) {
    // Add the main component code block
    if (component.sourceCode) {
      const tokens = tokenizeCode(component.sourceCode);
      const structure = extractCodeStructure(component.sourceCode);

      blocks.push({
        entitySlug: component.slug || component.name.toLowerCase(),
        entityName: component.name,
        code: component.sourceCode,
        filePath: component.filePath,
        startLine: component.declarationLineStart,
        endLine: component.declarationLineEnd,
        hash: simpleHash(component.sourceCode),
        tokens,
        structure
      });
    }

    // Add method code blocks
    if (component.methods) {
      for (const method of component.methods) {
        if (method.code) {
          const tokens = tokenizeCode(method.code);
          const structure = extractCodeStructure(method.code);

          blocks.push({
            entitySlug: component.slug || component.name.toLowerCase(),
            entityName: component.name,
            methodName: method.name,
            code: method.code,
            filePath: component.filePath,
            startLine: method.declarationLineStart,
            endLine: method.declarationLineEnd,
            hash: simpleHash(method.code),
            tokens,
            structure
          });
        }
      }
    }
  }

  return blocks;
}

export function detectDuplicates(
  codeBlocks: CodeBlock[],
  similarityThreshold = 0.8,
  exactDuplicateThreshold = 0.95
): DuplicateCodeMatch[] {
  const matches: DuplicateCodeMatch[] = [];
  const seen = new Set<string>();

  for (let i = 0; i < codeBlocks.length; i++) {
    for (let j = i + 1; j < codeBlocks.length; j++) {
      const blockA = codeBlocks[i];
      const blockB = codeBlocks[j];

      // Skip same entity comparisons
      if (blockA.entitySlug === blockB.entitySlug && blockA.methodName === blockB.methodName) {
        continue;
      }

      // Create unique match identifier to avoid duplicates
      const matchId = `${blockA.entitySlug}-${blockA.methodName || 'main'}-${blockB.entitySlug}-${blockB.methodName || 'main'}`;
      const reverseMatchId = `${blockB.entitySlug}-${blockB.methodName || 'main'}-${blockA.entitySlug}-${blockA.methodName || 'main'}`;

      if (seen.has(matchId) || seen.has(reverseMatchId)) {
        continue;
      }

      const similarity = calculateSimilarity(blockA, blockB);

      if (similarity >= similarityThreshold) {
        seen.add(matchId);

        let reason = '';
        if (similarity >= exactDuplicateThreshold) {
          reason = 'Exact or near-exact duplicate code detected';
        } else if (blockA.structure === blockB.structure) {
          reason = 'Similar code structure with different variable names';
        } else if (blockA.hash === blockB.hash) {
          reason = 'Identical code hash - potential exact duplicate';
        } else {
          reason = 'Similar code logic and patterns detected';
        }

        matches.push({
          sourceEntity: blockA.entitySlug,
          targetEntity: blockB.entitySlug,
          sourceMethod: blockA.methodName,
          targetMethod: blockB.methodName,
          similarity,
          reason,
          sourceCode: blockA.code,
          targetCode: blockB.code,
          sourceFile: blockA.filePath,
          targetFile: blockB.filePath,
          sourceLine: blockA.startLine,
          targetLine: blockB.startLine
        });
      }
    }
  }

  return matches.sort((a, b) => b.similarity - a.similarity);
}

function calculateSimilarity(blockA: CodeBlock, blockB: CodeBlock): number {
  // Exact hash match
  if (blockA.hash === blockB.hash) {
    return 1.0;
  }

  // Structure similarity
  const structureSimilarity = blockA.structure === blockB.structure ? 0.4 : 0;

  // Token-based similarity (Jaccard similarity)
  const tokenSimilarity = calculateJaccardSimilarity(blockA.tokens, blockB.tokens) * 0.4;

  // Length similarity
  const lengthSimilarity = calculateLengthSimilarity(blockA.code.length, blockB.code.length) * 0.2;

  return structureSimilarity + tokenSimilarity + lengthSimilarity;
}

function calculateJaccardSimilarity(tokensA: string[], tokensB: string[]): number {
  const setA = new Set(tokensA);
  const setB = new Set(tokensB);

  const intersection = new Set([...setA].filter(token => setB.has(token)));
  const union = new Set([...setA, ...setB]);

  return union.size === 0 ? 0 : intersection.size / union.size;
}

function calculateLengthSimilarity(lengthA: number, lengthB: number): number {
  const maxLength = Math.max(lengthA, lengthB);
  const minLength = Math.min(lengthA, lengthB);

  return maxLength === 0 ? 1 : minLength / maxLength;
}

function tokenizeCode(code: string): string[] {
  const tokens: string[] = [];

  try {
    const sourceFile = ts.createSourceFile(
      "temp.tsx",
      code,
      ts.ScriptTarget.Latest,
      true
    );

    function visit(node: ts.Node) {
      // Extract different types of tokens
      if (ts.isIdentifier(node)) {
        tokens.push(`ID:${node.text}`);
      } else if (ts.isStringLiteral(node)) {
        tokens.push('STRING_LITERAL');
      } else if (ts.isNumericLiteral(node)) {
        tokens.push('NUMERIC_LITERAL');
      } else if (ts.isToken(node)) {
        tokens.push(`TOKEN:${ts.SyntaxKind[node.kind]}`);
      }

      ts.forEachChild(node, visit);
    }

    ts.forEachChild(sourceFile, visit);
  } catch (error) {
    // Fallback to simple regex-based tokenization
    const simpleTokens = code
      .replace(/\/\*[\s\S]*?\*\//g, '') // Remove block comments
      .replace(/\/\/.*$/gm, '') // Remove line comments
      .match(/\b\w+\b/g) || [];

    tokens.push(...simpleTokens.filter(token => token.length > 1));
  }

  return tokens;
}

function visit(node: ts.Node) {
      // Extract different types of tokens
      if (ts.isIdentifier(node)) {
        tokens.push(`ID:${node.text}`);
      } else if (ts.isStringLiteral(node)) {
        tokens.push('STRING_LITERAL');
      } else if (ts.isNumericLiteral(node)) {
        tokens.push('NUMERIC_LITERAL');
      } else if (ts.isToken(node)) {
        tokens.push(`TOKEN:${ts.SyntaxKind[node.kind]}`);
      }

      ts.forEachChild(node, visit);
    }

function extractCodeStructure(code: string): string {
  const structure: string[] = [];

  try {
    const sourceFile = ts.createSourceFile(
      "temp.tsx",
      code,
      ts.ScriptTarget.Latest,
      true
    );

    function visit(node: ts.Node) {
      // Extract structural elements
      if (ts.isIfStatement(node)) {
        structure.push('IF');
      } else if (ts.isForStatement(node) || ts.isForInStatement(node) || ts.isForOfStatement(node)) {
        structure.push('FOR');
      } else if (ts.isWhileStatement(node) || ts.isDoStatement(node)) {
        structure.push('WHILE');
      } else if (ts.isSwitchStatement(node)) {
        structure.push('SWITCH');
      } else if (ts.isTryStatement(node)) {
        structure.push('TRY');
      } else if (ts.isFunctionDeclaration(node) || ts.isArrowFunction(node) || ts.isFunctionExpression(node)) {
        structure.push('FUNCTION');
      } else if (ts.isCallExpression(node)) {
        structure.push('CALL');
      } else if (ts.isReturnStatement(node)) {
        structure.push('RETURN');
      } else if (ts.isVariableDeclaration(node)) {
        structure.push('VAR');
      }

      ts.forEachChild(node, visit);
    }

    ts.forEachChild(sourceFile, visit);
  } catch (error) {
    // Fallback to regex patterns
    if (code.includes('if') || code.includes('else')) structure.push('IF');
    if (code.includes('for') || code.includes('while')) structure.push('LOOP');
    if (code.includes('function') || code.includes('=>')) structure.push('FUNCTION');
    if (code.includes('return')) structure.push('RETURN');
  }

  return structure.join('-');
}

