async function fetchComponentCounts() {
      try {
        const res = await fetch('/docs-data/component-index.json')
        const data = await res.json()

        // The component index already contains type information
        const componentDetails = data

        // Classify items by type based on naming patterns and characteristics
        const typeCounts = componentDetails.reduce((acc: ComponentCounts, comp: ComponentDetail) => {
          let type = 'component' // default

          // Determine type based on naming patterns and file paths
          const name = comp.name
          const filePath = comp.filePath || ''

          // UI components (from ui/ directory) 
          if (comp.slug?.startsWith('ui_') || filePath.includes('/ui/')) {
            if (name[0] === name[0].toUpperCase() && !name.includes('_')) {
              type = 'component' // PascalCase = React component
            } else {
              type = 'function' // camelCase = function/method
            }
          }
          // Functions and methods (camelCase names)
          else if (name[0] === name[0].toLowerCase() && /[a-z][A-Z]/.test(name) || 
                   name.includes('handle') || name.includes('fetch') || name.includes('get') || 
                   name.includes('set') || name.includes('toggle') || name.includes('render')) {
            type = 'function'
          }
          // Classes (usually PascalCase and might contain certain keywords)
          else if (name[0] === name[0].toUpperCase() && 
                   (name.includes('Service') || name.includes('Manager') || name.includes('Controller') || 
                    name.includes('Handler') || name.includes('Provider') && !filePath.includes('component'))) {
            type = 'class'  
          }
          // Components (PascalCase, typically React components)
          else if (name[0] === name[0].toUpperCase()) {
            type = 'component'
          }
          // Everything else defaults to function
          else {
            type = 'function'
          }

          // Override with explicit type if provided
          if (comp.type) {
            type = comp.type
          }

          if (type === 'component') {
            acc.components++
          } else if (type === 'class') {
            acc.classes++
          } else if (type === 'function') {
            acc.functions++
          } else if (type === 'method') {
            acc.methods++
          }

          return acc
        }, { components: 0, classes: 0, functions: 0, methods: 0, totalCoverage: 0 })

        const totalItems = componentDetails.length
        const itemsWithDescription = componentDetails.filter((comp: ComponentDetail) =>
          comp.description && comp.description.trim().length > 0
        ).length

        const coveragePercentage = totalItems > 0
          ? Math.round((itemsWithDescription / totalItems) * 100)
          : 0

        setCounts({
          ...typeCounts,
          totalCoverage: coveragePercentage
        })

        // Calculate additional stats
        const totalLinesEstimate = componentDetails.reduce((acc: number, comp: ComponentDetail) => {
          return acc + (comp.code ? comp.code.split('\n').length : 50) // Estimate 50 lines per component if no code
        }, 0)

        setStats({
          totalLines: totalLinesEstimate,
          lastUpdated: new Date().toLocaleDateString(),
          averageComplexity: Math.round(totalItems > 0 ? (typeCounts.methods / totalItems) * 10 : 0),
          documentedItems: itemsWithDescription
        })

        setLoading(false)
      } catch (error) {
        console.error('Error loading component data:', error)
        setLoading(false)
      }
    }

export function cosineSimilarity(vecA: number[], vecB: number[]): number {
  if (vecA.length !== vecB.length) {
    throw new Error("Vectors must have the same length");
  }
  let dotProduct = 0;
  let normA = 0;
  let normB = 0;
  for (let i = 0; i < vecA.length; i++) {
    dotProduct += vecA[i] * vecB[i];
    normA += vecA[i] * vecA[i];
    normB += vecB[i] * vecB[i];
  }
  if (normA === 0 || normB === 0) {
    return 0;
  }
  return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
}

